#include <stdint.h>
#include "main.h"


int main(void)
{
	RCC_AHB1ENR_t volatile *const pClkCtrlReg = (RCC_AHB1ENR_t*) 0x40023830;
	GPIOx_MODER_t volatile *const pPortDModeReg = (GPIOx_MODER_t*) 0x40020C00;
	GPIOx_ODR_t volatile *const pPortDOutRef = (GPIOx_ODR_t*) 0x40020C14;

	//1. Enable AHB1 peripheral GPIOD without affecting other peripheral hence we use |
	// We only affect the third bit
	pClkCtrlReg->gpioD_en = 1;

	//2. Configure the mode of the GPIO D port
	pPortDModeReg->pin_12 = 1;

    /* Loop forever */
	for(;;){
		//3. Finally set output value to 1
		pPortDOutRef->pin_12 = 1;

		//Adding delay
		for( uint32_t i=0 ; i<10000000;i++);

		//Turn LED off
		pPortDOutRef->pin_12 = 0;

		//Adding delay
		for( uint32_t i=0 ; i<10000000;i++);



	}
}
