#include <stdint.h>
#include <stdio.h>


int main(void)
{
	uint32_t volatile *const rccGpioD_en = (uint32_t *) 0x40023830;
	uint32_t volatile *const gpioD_Mod = (uint32_t *) 0x40020C00;
	uint32_t volatile *const gpioD_PUPDR = (uint32_t *) 0x40020C0C;
	uint32_t volatile *const gpioD_IN = (uint32_t *) 0x40020C10;
	uint32_t volatile *const gpioD_OUT = (uint32_t *) 0x40020C14;

	//Enabling GPIOD clock
	*rccGpioD_en |= (1 << 3);

	//Setting Mode for Input and Output pins
	//Input 8, 9, 10, 11
	*gpioD_Mod &= ~(0xFF << 16);

	//Output 0, 1, 2, 3
	*gpioD_Mod &= ~0xFF;
	*gpioD_Mod |= 0x55;

	//Setting pull up registers
	*gpioD_PUPDR &= ~(0xFF << 16);
	*gpioD_PUPDR |= (0x55 << 16);

	int row = 0;
	char key[4][4] = {{'1','2','3','A'},{'4','5','6','B'},{'7','8','9','C'},{'*','0','#','D'}};

	/* Loop forever */
	for(;;){
		//Start all outputs high
		*gpioD_OUT |= 0xF;

		if (row >3){
			row=0;
		}

		//Make first row low
		*gpioD_OUT &= ~(1<<row);

		//Read inputs
		for (int col=0; col<4; col++){
			if (! ( (*gpioD_IN >> (8+col)) & 1 ) ){
				printf("%c\n",key[row][col]);
				//delay
				for( uint32_t i=0 ; i<300000;i++);
			}
		}

		row++;

	}
}
