#include <stdint.h>
#include "main.h"


int main(void)
{
	RCC_AHB1ENR_t volatile *const pClkCtrlReg = ADDR_REG_AHB1ENR;
	GPIOx_MODER_t volatile *const pPortDModeReg = ADDR_REG_GPIOD_MODER;
	GPIOx_ODR_t volatile *const pPortDOutRef = ADDR_REG_GPIOD_ODR;

	//1. Enable AHB1 peripheral GPIOD without affecting other peripheral hence we use |
	// We only affect the third bit
	pClkCtrlReg->gpioD_en = CLOCK_ENABLE;

	//2. Configure the mode of the GPIO D port
	pPortDModeReg->pin_12 = MODE_CONF_OUTPUT;

    /* Loop forever */
	for(;;){
		//3. Finally set output value to 1
		pPortDOutRef->pin_12 = PIN_STATE_HIGH;

		//Adding delay
		for( uint32_t i=0 ; i<DELAY_COUNT;i++);

		//Turn LED off
		pPortDOutRef->pin_12 = PIN_STATE_LOW;

		//Adding delay
		for( uint32_t i=0 ; i<DELAY_COUNT;i++);



	}
}
